import django
from django.core.handlers.wsgi import WSGIHandler


def get_wsgi_application():
    """
    The public3 interface to Django's WSGI support. Return a WSGI callable.

    Avoids making django.core.handlers.WSGIHandler a public3 API, in case the
    internal WSGI implementation changes or moves in the future.
    """
    django.setup(set_prefix=False)
    return WSGIHandler()
